var dblite = require('dblite'),
    db = dblite('./tags.db');

db
  // silent exit (no bye-bye message)
  .on('close', Object)
  // error handler
  .on('error', function(err) {
    console.log('something terrible happened, rebooting', err);
    process.exit();
  })
  // show databases
  .query('.databases')
  // exit
  .query('.quit')
;